    private CardState cardState;

    protected override void OnInitialized()
    {
        this.cardState = new CardState();

        for (int i = 0; i < 5; i++)
        {
            var schoolCard = new SchoolCard();
            schoolCard.Time.TextBehavior.Value = "VYT";

            var area = new DropAreaCard();
            area.PositionBehavior.Position = new BlazeCardsCore.Models.Vector2f(i * 200, 0);
            area.SizeBehavior.Size = new BlazeCardsCore.Models.Vector2f(200, 1000);
            area.Clickable = false;
            area.Draggable = false;
            area.Visible = false;
            area.Classes.Add("school-drop-area");
            var list = new VerticalListCard();
            list.Clickable = false;
            list.Draggable = false;

            area.OnDrop += (o, e) =>
            {
                foreach (var item in e.Cards)
                {
                    if (list != item.Parent)
                    {
                        item.Parent.UnhookChild(item);
                        list.AddChild(item);
                    }
                    if (item is TextCard)
                    {
                        var downText = item as TextCard;
                        downText.TextBehavior.Value = downText.PositionBehavior.Position.Y.ToString();
                    }
                }
            };

            list.PositionBehavior.Position = new BlazeCardsCore.Models.Vector2f(i * 200, 0);

            //list.AddChild(area);
            list.AddChild(schoolCard);

            this.cardState.Storage.Cards.Add(area);
            this.cardState.Storage.Cards.Add(list);
            //for (int x = 0; x < 19; x++)
            //{
            //    var nothing = new RectCard();
            //    list.AddChild(nothing);
            //}
        }
    }
